import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Footer from '../components/Footer';

const Projects = () => {
  const [albums, setAlbums] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchAlbums();
  }, []);

  const fetchAlbums = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/albums`);
      if (!response.ok) {
        throw new Error('Failed to fetch albums');
      }
      const data = await response.json();
      console.log('Albums data:', data);
      setAlbums(data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching albums:', err);
      setError(err.message);
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center w-full">
        <div className="text-xl text-gray-600">Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center w-full">
        <div className="text-xl text-red-600">Error: {error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col w-full">
      <div className="flex-1 w-full">
        <div className="container mx-auto px-2 py-4">
          <h1 className="text-2xl sm:text-3xl font-bold text-gray-900 mb-4 text-center">Projects</h1>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2 justify-items-center">
            {albums.map((album) => (
              <Link
                key={album.id}
                to={`/album/${album.id}`}
                className="w-full max-w-[300px] group"
              >
                <div className="relative pt-[100%] bg-gray-100 rounded-md overflow-hidden">
                  <img
                    src={`${process.env.REACT_APP_API_URL}/${album.image}`}
                    alt={album.name}
                    className="absolute inset-0 w-full h-full object-cover transition-transform duration-300 group-hover:scale-105"
                    onError={(e) => {
                      console.error(`Error loading image: ${album.image}`);
                      e.target.src = 'data:image/svg+xml;base64,';
                    }}
                  />
                </div>
                <h2 className="mt-2 text-lg font-semibold text-gray-900 text-center">{album.name}</h2>
              </Link>
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Projects;
