import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';

const Assignment = () => {
  const [albums, setAlbums] = useState([]);
  const [images, setImages] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const { id } = useParams();

  useEffect(() => {
    if (id) {
      fetchImages();
    } else {
      fetchAlbums();
    }
  }, [id]);

  const fetchAlbums = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/albums`);
      if (!response.ok) {
        throw new Error('Failed to fetch albums');
      }
      const data = await response.json();
      console.log('Albums data:', data);
      
      if (Array.isArray(data)) {
        setAlbums(data);
      } else {
        setError('Invalid data format received from server');
        setAlbums([]);
      }
    } catch (err) {
      setError(err.message || 'Failed to fetch albums');
      setAlbums([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchImages = async () => {
    try {
      setLoading(true);
      console.log('Fetching images for album:', id);
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/images/album/${id}`);
      console.log('Response status:', response.status);
      
      if (!response.ok) {
        const errorData = await response.json();
        console.error('Error response:', errorData);
        throw new Error(errorData.message || 'Failed to fetch images');
      }
      
      const data = await response.json();
      console.log('Images data received:', data);
      
      if (Array.isArray(data)) {
        setImages(data);
      } else {
        console.error('Invalid data format:', data);
        setError('Invalid data format received from server');
        setImages([]);
      }
    } catch (err) {
      console.error('Error in fetchImages:', err);
      setError(err.message || 'Failed to fetch images');
      setImages([]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="max-w-[2000px] mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="text-center">Loading...</div>
      </div>
    );
  }

  if (id) {
    return (
      <div className="max-w-[2000px] mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex items-center mb-8">
          <Link to="/assignment" className="text-blue-600 hover:text-blue-800 mr-4">
            ‚Üê Back to Albums
          </Link>
          <h1 className="text-3xl font-bold text-gray-900">Album Images</h1>
        </div>

        {error ? (
          <div className="text-red-600 text-center mb-8">{error}</div>
        ) : images.length === 0 ? (
          <div className="text-gray-600 text-center">No images available in this album</div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {images.map((image) => (
              <div 
                key={image.id} 
                className="bg-white shadow rounded-[19px] overflow-hidden opacity-0 animate-fade-in-up hover:shadow-lg transition-all duration-300"
              >
                <img 
                  src={`${process.env.REACT_APP_API_URL}${image.imageUrl}`}
                  alt={image.description || 'Album image'} 
                  className="w-full h-64 object-cover rounded-t-[19px]"
                  onError={(e) => {
                    console.log('Image error:', image.imageUrl);
                    e.target.onerror = null;
                    e.target.src = 'data:image/svg+xml;base64,';
                  }}
                />
                {image.description && (
                  <div className="p-4">
                    <p className="text-gray-700">{image.description}</p>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    );
  }

  return (
    <div className="max-w-[2000px] mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-3xl font-bold text-gray-900 mb-8">Our Albums</h1>

      {error ? (
        <div className="text-red-600 text-center mb-8">{error}</div>
      ) : albums.length === 0 ? (
        <div className="text-gray-600 text-center">No albums available</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {albums.map((album) => (
            <Link
              key={album.id}
              to={`/assignment/${album.id}`}
              className="bg-white shadow rounded-[19px] overflow-hidden hover:shadow-lg transition-all duration-300 opacity-0 animate-fade-in-up"
            >
              <img 
                src={`${process.env.REACT_APP_API_URL}${album.image}`}
                alt={album.name} 
                className="w-full h-48 object-cover rounded-t-[19px]"
                onError={(e) => {
                  console.log('Album image error:', album.image);
                  e.target.onerror = null;
                  e.target.src = 'data:image/svg+xml;base64,';
                }}
              />
              <div className="p-4 bg-white rounded-[19px]">
                <h3 className="text-lg font-semibold text-gray-900">{album.name}</h3>
                <p className="text-sm text-gray-600 mt-1">Click to view images</p>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default Assignment;
