import React, { useState } from 'react';
import { FaEnvelope, FaInstagram, FaLinkedin, FaFacebook } from 'react-icons/fa';
import Footer from '../components/Footer';

const Contact = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    surname: '',
    email: '',
    subject: '',
    inquiry: ''
  });

  const images = Array.from({ length: 12 }, (_, i) => `/c${i + 1}.jpg`);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const mailtoLink = `mailto:fbouazi3@gmail.com?subject=${encodeURIComponent(formData.subject)}&body=${encodeURIComponent(
      `From: ${formData.firstName} ${formData.surname}\nEmail: ${formData.email}\n\n${formData.inquiry}`
    )}`;
    window.location.href = mailtoLink;
  };

  return (
    <div className="min-h-screen flex flex-col w-screen overflow-x-hidden">
      <div className="flex-grow w-full bg-white">
        <div className="w-full px-4 sm:px-6 lg:px-8 py-6 sm:py-8 lg:py-12">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 lg:gap-12">
            {/* Formulaire et Grille d'images */}
            <div className="lg:col-span-2 w-full space-y-8">
              <h1 className="text-3xl sm:text-4xl font-bold text-gray-900 mb-6 sm:mb-8">Contact</h1>
              
              {/* Formulaire */}
              <form onSubmit={handleSubmit} className="w-full space-y-4 sm:space-y-6">
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 sm:gap-6">
                  <input
                    type="text"
                    name="firstName"
                    placeholder="First given name"
                    value={formData.firstName}
                    onChange={handleChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-base"
                    required
                  />
                  <input
                    type="text"
                    name="surname"
                    placeholder="Surname"
                    value={formData.surname}
                    onChange={handleChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-base"
                    required
                  />
                </div>
                <input
                  type="email"
                  name="email"
                  placeholder="Email address"
                  value={formData.email}
                  onChange={handleChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-base"
                  required
                />
                <input
                  type="text"
                  name="subject"
                  placeholder="Subject"
                  value={formData.subject}
                  onChange={handleChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-base"
                  required
                />
                <textarea
                  name="inquiry"
                  placeholder="Inquiry"
                  value={formData.inquiry}
                  onChange={handleChange}
                  rows="6"
                  className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-base"
                  required
                />
                <button
                  type="submit"
                  className="w-full sm:w-auto min-w-[200px] px-8 py-4 bg-gray-800 text-white rounded-md hover:bg-gray-700 transition-colors text-base font-semibold"
                >
                  Submit
                </button>
              </form>

              {/* Grille d'images */}
              <div className="w-full mt-8 sm:mt-12">
                <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-3 sm:gap-4">
                  {images.map((image, index) => (
                    <div key={index} className="relative pt-[100%] bg-gray-100 rounded-lg overflow-hidden group">
                      <img
                        src={image}
                        alt={`Contact ${index + 1}`}
                        className="absolute inset-0 w-full h-full object-cover rounded-lg shadow-md transition-all duration-300 group-hover:scale-110"
                        onError={(e) => {
                          console.error(`Error loading image: ${image}`);
                          e.target.src = 'data:image/svg+xml;base64,';
                        }}
                      />
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Quick Contact */}
            <div className="lg:col-span-1 w-full">
              <div className="sticky top-8 w-full bg-gray-50 p-6 rounded-lg">
                <h2 className="text-2xl sm:text-3xl font-bold text-gray-900 mb-6">Quick Contact</h2>
                <a 
                  href="mailto:charaf.lahib@gmail.com" 
                  className="text-lg text-gray-700 hover:text-gray-900 mb-6 block hover:underline transition-colors"
                >
                  charaf.lahib@gmail.com
                </a>
                <div className="flex justify-start space-x-6 mt-6">
                  <a 
                    href="mailto:charaf.lahib@gmail.com" 
                    className="text-gray-700 hover:text-gray-900 transition-colors p-2 hover:bg-gray-200 rounded-full"
                    aria-label="Email"
                  >
                    <FaEnvelope size={28} />
                  </a>
                  <a 
                    href="https://www.instagram.com/charaflahib" 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="text-gray-700 hover:text-gray-900 transition-colors p-2 hover:bg-gray-200 rounded-full"
                    aria-label="Instagram"
                  >
                    <FaInstagram size={28} />
                  </a>
                  <a 
                    href="https://www.linkedin.com/in/charaflahib" 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="text-gray-700 hover:text-gray-900 transition-colors p-2 hover:bg-gray-200 rounded-full"
                    aria-label="LinkedIn"
                  >
                    <FaLinkedin size={28} />
                  </a>
                  <a 
                    href="https://www.facebook.com/charaflahib" 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="text-gray-700 hover:text-gray-900 transition-colors p-2 hover:bg-gray-200 rounded-full"
                    aria-label="Facebook"
                  >
                    <FaFacebook size={28} />
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Contact;
