import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';

const Assignment = () => {
  const [albums, setAlbums] = useState([]);
  const [images, setImages] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const { id } = useParams();
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);

  useEffect(() => {
    if (id) {
      fetchImages();
    } else {
      fetchAlbums();
    }
  }, [id]);

  const fetchAlbums = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/albums`);
      if (!response.ok) {
        throw new Error('Failed to fetch albums');
      }
      const data = await response.json();
      console.log('Albums data:', data);
      
      if (Array.isArray(data)) {
        setAlbums(data);
      } else {
        setError('Invalid data format received from server');
        setAlbums([]);
      }
    } catch (err) {
      setError(err.message || 'Failed to fetch albums');
      setAlbums([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchImages = async () => {
    try {
      setLoading(true);
      console.log('Fetching images for album:', id);
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/images/album/${id}`);
      console.log('Response status:', response.status);
      
      if (!response.ok) {
        const errorData = await response.json();
        console.error('Error response:', errorData);
        throw new Error(errorData.message || 'Failed to fetch images');
      }
      
      const data = await response.json();
      console.log('Images data received:', data);
      
      if (Array.isArray(data)) {
        setImages(data);
      } else {
        console.error('Invalid data format:', data);
        setError('Invalid data format received from server');
        setImages([]);
      }
    } catch (err) {
      console.error('Error in fetchImages:', err);
      setError(err.message || 'Failed to fetch images');
      setImages([]);
    } finally {
      setLoading(false);
    }
  };

  const nextImage = () => {
    setIsTransitioning(true);
    setTimeout(() => {
      setCurrentImageIndex((prev) => (prev + 1) % images.length);
      setIsTransitioning(false);
    }, 300);
  };

  const previousImage = () => {
    setIsTransitioning(true);
    setTimeout(() => {
      setCurrentImageIndex((prev) => (prev - 1 + images.length) % images.length);
      setIsTransitioning(false);
    }, 300);
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="text-center">Loading...</div>
      </div>
    );
  }

  if (id) {
    return (
      <div className="max-w-[2000px] mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex items-center mb-8">
          <Link to="/assignment" className="text-blue-600 hover:text-blue-800 mr-4">
            ← Back to Albums
          </Link>
          <h1 className="text-3xl font-bold text-gray-900">Album Images</h1>
        </div>

        {error ? (
          <div className="text-red-600 text-center mb-8">{error}</div>
        ) : images.length === 0 ? (
          <div className="text-gray-600 text-center">No images available in this album</div>
        ) : (
          <div className="relative w-full max-w-4xl mx-auto">
            {/* Image principale */}
            <div className="relative aspect-[16/9] bg-black rounded-xl overflow-hidden">
              <img 
                src={`${process.env.REACT_APP_API_URL}${images[currentImageIndex].imageUrl}`}
                alt={images[currentImageIndex].description || 'Album image'} 
                className={`w-full h-full object-contain transition-opacity duration-300 ${
                  isTransitioning ? 'opacity-0' : 'opacity-100'
                }`}
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = 'data:image/svg+xml;base64,';
                }}
              />

              {/* Boutons de navigation */}
              <button 
                onClick={previousImage}
                className="absolute left-4 top-1/2 -translate-y-1/2 bg-black/50 hover:bg-black/70 text-white p-3 rounded-full transition-all duration-300 transform hover:scale-110"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
              </button>

              <button 
                onClick={nextImage}
                className="absolute right-4 top-1/2 -translate-y-1/2 bg-black/50 hover:bg-black/70 text-white p-3 rounded-full transition-all duration-300 transform hover:scale-110"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </button>
            </div>

            {/* Indicateur de progression */}
            <div className="flex justify-center mt-4 space-x-2">
              {images.map((_, index) => (
                <div 
                  key={index}
                  className={`w-2 h-2 rounded-full transition-all duration-300 ${
                    index === currentImageIndex ? 'bg-blue-600 w-4' : 'bg-gray-300'
                  }`}
                />
              ))}
            </div>

            {/* Description de l'image */}
            {images[currentImageIndex].description && (
              <div className="mt-4 text-center">
                <p className="text-gray-700">{images[currentImageIndex].description}</p>
              </div>
            )}
          </div>
        )}
      </div>
    );
  }

  return (
    <div className="max-w-[2000px] mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-3xl font-bold text-gray-900 mb-8">Our Albums</h1>

      {error ? (
        <div className="text-red-600 text-center mb-8">{error}</div>
      ) : albums.length === 0 ? (
        <div className="text-gray-600 text-center">No albums available</div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {albums.map((album, index) => (
            <Link
              key={album.id}
              to={`/assignment/${album.id}`}
              className="bg-white shadow rounded-lg overflow-hidden hover:shadow-lg transition-all duration-300 animate-fade-in group"
              style={{
                animationDelay: `${index * 150}ms`,
                opacity: 0,
                animation: 'fadeIn 0.6s ease-out forwards'
              }}
            >
              <img 
                src={`${process.env.REACT_APP_API_URL}${album.image}`}
                alt={album.name} 
                className="w-full h-48 object-cover"
                onError={(e) => {
                  console.log('Album image error:', album.image);
                  e.target.onerror = null;
                  e.target.src = 'data:image/svg+xml;base64,';
                }}
              />
              <div className="p-4 text-center">
                <h3 className="text-lg font-semibold text-gray-900 mb-2">{album.name}</h3>
                <p className="text-sm text-gray-500 relative inline-block">
                  <span className="relative inline-block after:content-[''] after:absolute after:bottom-0 after:left-0 after:w-0 after:h-[1px] after:bg-gray-500 after:transition-all after:duration-500 group-hover:after:w-full">
                    Click to view images
                  </span>
                </p>
              </div>
            </Link>
          ))}
        </div>
      )}

      <style jsx>{`
        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
};

export default Assignment;
