import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import Footer from '../components/Footer';

const Projects = () => {
  const [albums, setAlbums] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const containerRef = useRef(null);

  // Projets statiques
  const staticProjects = [
    {
      title: 'Vimeo Video (904392431)',
      url: 'https://vimeo.com/904392431',
      thumbnail: 'bassma.png',
      description: 'Watch our Vimeo video!'
    },
    // ... ajoutez vos autres projets statiques ici
  ];

  useEffect(() => {
    fetchAlbums();
  }, []);

  // Animation d'apparition
  useEffect(() => {
    if (!containerRef.current) return;

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.style.opacity = 1;
            entry.target.style.transform = 'scale(1)';
          }
        });
      },
      { threshold: 0.1 }
    );

    const projectCards = containerRef.current.querySelectorAll('.project-card');
    projectCards.forEach(card => observer.observe(card));

    return () => {
      projectCards.forEach(card => observer.unobserve(card));
    };
  }, [albums]);

  const fetchAlbums = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/albums`);
      if (!response.ok) throw new Error('Failed to fetch albums');
      const data = await response.json();
      setAlbums(data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching albums:', err);
      setError(err.message);
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center w-full">
        <div className="text-xl text-gray-600">Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center w-full">
        <div className="text-xl text-red-600">Error: {error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col w-full">
      <style>{`
        .project-card {
          position: relative;
          overflow: hidden;
          border-radius: 8px;
          background-color: #fff;
          transition: transform 0.3s, opacity 0.3s;
          transform: scale(0.95);
          opacity: 0;
          cursor: pointer;
        }
        .project-card .overlay {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background-color: rgba(0, 0, 0, 0.7);
          opacity: 0;
          transition: opacity 0.3s ease;
          display: flex;
          align-items: center;
          justify-content: center;
          color: #fff;
          text-align: center;
          padding: 10px;
        }
        .project-card:hover .overlay {
          opacity: 1;
        }
        .project-card:hover img {
          transform: scale(1.05);
        }
      `}</style>

      <div className="max-w-[1600px] mx-auto px-4 py-8 w-full" ref={containerRef}>
        <h1 className="text-3xl font-bold text-gray-900 mb-8 text-left">Projects</h1>
        
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {/* Projets statiques */}
          {staticProjects.map((project, index) => (
            <a
              key={`static-${index}`}
              href={project.url}
              target="_blank"
              rel="noopener noreferrer"
              className="project-card"
            >
              <div className="relative h-[250px]">
                <img
                  src={project.thumbnail}
                  alt={project.title}
                  className="w-full h-full object-cover transition-transform duration-300"
                />
                <div className="overlay">
                  <p className="text-base">{project.description}</p>
                </div>
              </div>
              <h3 className="py-4 px-3 text-lg font-semibold text-center">
                {project.title}
              </h3>
            </a>
          ))}

          {/* Albums dynamiques */}
          {albums.map((album) => (
            <Link
              key={album.id}
              to={`/album/${album.id}`}
              className="project-card"
            >
              <div className="relative h-[250px]">
                <img
                  src={`${process.env.REACT_APP_API_URL}/${album.image}`}
                  alt={album.name}
                  className="w-full h-full object-cover transition-transform duration-300"
                  onError={(e) => {
                    e.target.src = 'data:image/svg+xml;base64,';
                  }}
                />
                <div className="overlay">
                  <p className="text-base">View Album</p>
                </div>
              </div>
              <h3 className="py-4 px-3 text-lg font-semibold text-center">
                {album.name}
              </h3>
            </Link>
          ))}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Projects;
