import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const AlbumImages = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [album, setAlbum] = useState(null);
  const [images, setImages] = useState([]);
  const [newImage, setNewImage] = useState({
    description: '',
    imageUrl: '',
    link: '',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAlbum();
    fetchImages();
  }, [id]);

  const fetchAlbum = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/albums/${id}`);
      if (!response.ok) throw new Error('Failed to fetch album');
      const albumData = await response.json();
      setAlbum(albumData);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchImages = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/images?albumId=${id}`);
      if (!response.ok) throw new Error('Failed to fetch images');
      const imagesData = await response.json();
      setImages(imagesData);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('description', newImage.description);
      formData.append('link', newImage.link);
      formData.append('image', newImage.file);
      formData.append('albumId', id);

      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/images`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: formData,
      });

      if (response.ok) {
        setNewImage({ description: '', imageUrl: '', link: '' });
        fetchImages();
      } else {
        setError('Failed to create image');
      }
    } catch (err) {
      setError('An error occurred');
    }
  };

  const handleDelete = async (imageId) => {
    if (window.confirm('Are you sure you want to delete this image?')) {
      try {
        const response = await fetch(`${process.env.REACT_APP_API_URL}/api/images/${imageId}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          },
        });

        if (response.ok) {
          fetchImages();
        } else {
          setError('Failed to delete image');
        }
      } catch (err) {
        setError('An error occurred');
      }
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Images in {album.name}</h1>
        <button
          onClick={() => navigate('/admin/albums')}
          className="text-blue-600 hover:text-blue-800"
        >
          Back to Albums
        </button>
      </div>

      {/* Create Image Form */}
      <div className="bg-white shadow rounded-lg p-6 mb-8">
        <h2 className="text-xl font-semibold mb-4">Add New Image</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Description</label>
            <textarea
              value={newImage.description}
              onChange={(e) => setNewImage({ ...newImage, description: e.target.value })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Image URL</label>
            <input
              type="url"
              value={newImage.imageUrl}
              onChange={(e) => setNewImage({ ...newImage, imageUrl: e.target.value })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Link (optional)</label>
            <input
              type="url"
              value={newImage.link}
              onChange={(e) => setNewImage({ ...newImage, link: e.target.value })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            />
          </div>
          <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
          >
            Add Image
          </button>
        </form>
      </div>

      {/* Images Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {images.map((image) => (
          <div key={image.id} className="bg-white shadow rounded-lg overflow-hidden">
            <img src={image.imageUrl} alt={image.description} className="w-full h-48 object-cover" />
            <div className="p-4">
              <p className="text-gray-700">{image.description}</p>
              {image.link && (
                <a
                  href={image.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 hover:text-blue-800 mt-2 block"
                >
                  View Link
                </a>
              )}
              <button
                onClick={() => handleDelete(image.id)}
                className="mt-4 text-red-600 hover:text-red-800"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      {error && (
        <div className="mt-4 text-red-600 text-center">{error}</div>
      )}
    </div>
  );
};

export default AlbumImages;
