import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Assignments = () => {
  const [albums, setAlbums] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAlbums();
  }, []);

  const fetchAlbums = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/albums`);
      if (!response.ok) {
        throw new Error('Failed to fetch albums');
      }
      const data = await response.json();
      
      if (Array.isArray(data)) {
        setAlbums(data);
      } else {
        setError('Invalid data format received from server');
        setAlbums([]);
      }
    } catch (err) {
      setError(err.message || 'Failed to fetch albums');
      setAlbums([]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="text-center">Loading...</div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-3xl font-bold text-gray-900 mb-8">Our Assignments</h1>

      {error ? (
        <div className="text-red-600 text-center mb-8">{error}</div>
      ) : albums.length === 0 ? (
        <div className="text-gray-600 text-center">No assignments available</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {albums.map((album) => (
            <Link
              key={album.id}
              to={`/assignment/${album.id}`}
              className="bg-white shadow rounded-lg overflow-hidden hover:shadow-lg transition-shadow"
            >
              <img 
                src={album.image} 
                alt={album.name} 
                className="w-full h-48 object-cover"
                onError={(e) => {
                  e.target.src = 'https://via.placeholder.com/400x300?text=No+Image';
                }}
              />
              <div className="p-4">
                <h3 className="text-lg font-semibold text-gray-900">{album.name}</h3>
                <p className="text-sm text-gray-600 mt-1">Click to view details</p>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default Assignments;
